// SPDX-License-Identifier: CC-BY-4.0
pragma solidity >=0.4.22 <0.9.0;

library Dispute {
    struct Item {
        string opendate;
        string  description;
        uint    amount;
        uint    balance;
        uint    limit;
        string  paystatus;
    }

    struct ReportItem {
        address subscriber;
        bytes   item;
    }

    struct Dispute {
        uint index;
        address subscriber;
        address consumer;
        string  disputeDate;
        ReportItem item;
        string reason;
        string status;
    }


    struct DisputeStorage {
        mapping(address => Dispute[]) disputes;
    }

    function disputeStorage() internal pure returns (DisputeStorage storage ds)
    {
        bytes32 position = keccak256("dispute.storage");
        assembly { ds.slot := position }
    }

}
