// SPDX-License-Identifier: CC-BY-4.0
pragma solidity >=0.4.22 <0.9.0;

import "./TransferAgent.sol";

abstract contract TransferAgentResponsibilities is TransferAgent {
    event ExemptOffering(address indexed from,string status, uint256 value);

    uint256 private parValue = 0;
    uint256 private start_timestamp;

    function setParValue(uint256 value)
        public
        payable
        onlyTransferAgent
    {
        parValue = value;
    }


    function toggleExemptOffering(string memory name, uint256 timestamp, bool _active)
        public
        onlyTransferAgent
    {
        start_timestamp = timestamp;
        active = _active;

        if (active) {
            emit ExemptOffering(msg.sender, string(abi.encodePacked(name, " has started")), timestamp);
            if (start_timestamp != 0) {
                _mint(msg.sender, INITIAL_SUPPLY);
            }
        } else {
            emit ExemptOffering(msg.sender, string(abi.encodePacked(name, " has stopped")), timestamp);
        }
    }


    /**
     * As each token is minted it is added to the shareholders array.
     * @param _to The address that will receive the minted tokens.
     * @param _amount The amount of tokens to mint.
     * @return A boolean that indicates if the operation was successful.
     */
    function mint(address _to, uint256 _amount, uint256 _epoch)
        public
        isActive
        onlyTransferAgent
        isMinter
        isVerifiedAddress(_to)
        returns (bool)
    {
        // if the address does not already own share then
        // add the address to the shareholders array and record the index.
        require(_epoch >= block.timestamp, "time spoofing error");
        updateShareholders(_to);
        uint256 _shares = _amount / parValue;
        addTransaction(_to, _shares, _amount, _epoch);
        return _mint(_to, _amount);
    }




    /**
     *  The `transfer` function MUST NOT allow transfers to addresses that
     *  have not been verified and added to the contract.
     *  If the `to` address is not currently a shareholder then it MUST become one.
     *  If the transfer will reduce `msg.sender`'s balance to 0 then that address
     *  MUST be removed from the list of shareholders.
     */
    function transfer(address to, uint256 value, uint256 epoch)
        public
        isActive
        isVerifiedAddress(to)
        isHoldingPeriodOver(to)
        returns (bool)
    {
        updateShareholders(to);
        pruneShareholders(msg.sender, value);
        uint256 _shares = value / parValue;
        addTransaction(to, _shares, value, epoch);
        addTransaction(msg.sender, _shares, negative * value, epoch);

        return _transfer(to, value);
    }

    /**
     *  The `transferFrom` function MUST NOT allow transfers to addresses that
     *  have not been verified and added to the contract.
     *  If the `to` address is not currently a shareholder then it MUST become one.
     *  If the transfer will reduce `from`'s balance to 0 then that address
     *  MUST be removed from the list of shareholders.
     */
    function transferFrom(address from, address to, uint256 value, uint256 epoch)
        public
        isActive
        isVerifiedAddress(to)
        isHoldingPeriodOver(from)
        returns (bool)
    {
        updateShareholders(to);
        pruneShareholders(from, value);
        uint256 _shares = value / parValue;
        addTransaction(to, _shares, value, epoch);
        addTransaction(from, _shares, negative * value, epoch);

        return _transferFrom(from, to, value);
    }

    // Internal
    function _mint(address addr, uint256 amount)
      internal
      returns (bool)
    {
      require(amount <= INITIAL_SUPPLY, "exceeds total supply");
      balances[addr] += amount;
      emit Minted(addr, amount);
      return true;
    }

    function _transfer(address addr, uint256 amount)
      internal
      returns (bool)
    {
      balances[addr] = amount;
      emit Transfer(addr, amount);
      return true;
    }

    function _transferFrom(address from,address to,uint256 value)
      internal
      returns (bool)
    {
      require(balances[from] > value, "insufficient balance");
      balances[to] += value;
      balances[from] -= value;
      emit TransferFrom(from, to, value);
      return true;
    }
}

